if ( $(".print-area").length ) {
    $("<style>")
    .prop("type", "text/css")
    .html("\
    @media print {\
        body {\
            position: relative;\
            margin: 0;\
            height:100vh;\
            overflow: visible;\
        }\
        body * {\
            visibility: hidden;\
            position: absolute;\
            left: 0px;\
            right: 0px;\
            top: 0px;\
            width: 100%;\
            break-inside: avoid;\
        }\
        .print-area, .print-area *{\
            visibility: visible;\
            position: relative;\
            width: 100%;\
            overflow: visible;\
            break-inside: avoid;\
        }\
        @page {size: A4;margin-left: 20mm;margin-right: 20mm;margin-top: 18mm;margin-bottom: 18mm;overflow: visible;}\
    }")
    .appendTo("body");
} 

